import React from "react";

function Bolt({bolt}){
    //return JSX elememts
    return(
        <div id="bolt">
            <div>
                <img src={bolt.avatar_url} />
                <h3>{bolt.name}</h3>
                <h4>{bolt.catchphrase}</h4>
            </div>
            <div id="bolt-condition">
                <h5>{bolt.health}</h5>
                <h5>{bolt.damage}</h5>
                <h5>{bolt.armor}</h5>
            </div>
        </div>

    )
}
export default Bolt;
