import React from "react";
import Bolt from "./Bolt";

function BoltList({bolts}){
    //a variable that will hold the array of data
    const boltItem = bolts.map(bolt =>{
        return(
            <Bolt key={bolt.id} bolt={bolt} />
        )
    })
    return(
        <div>
            {/* call the variable */}
            {boltItem}
        </div>

    )
}
export default BoltList;
