import logo from './logo.svg';
import './App.css';
import React, {useEffect, useState} from 'react';
import BoltList from './components/BoltList';

//declare a variable that holds the url
const url = ' http://localhost:3000/bots'

function App() {
  //declare a current state and the state function
  const [bolts, setBolts] = useState([])

  //use useEffect to fetch our data
  useEffect(()=>{
    fetch(url)
    .then(res => res.json())
    .then(data => setBolts(data))
  })

  return (
    <div className="App">
      <BoltList bolts={bolts}/>

    </div>
  );
}

export default App;
